clc;clear all;close all;# Affichage image et rotation 90° A = imread("kawaii_ice.jpg"); % On enregistre l'image sous forme matricielle# imshow(A) % Afficher l'image for y=1:rows(A)  for x=1:columns(A)    rotation = 90;    radian = (rotation * pi)/180;     newX = ((x - columns(A)/2) * cos(radian)) + ((y - columns(A)/2) * (-sin(radian)))     newY = ((x - rows(A) / 2) * (sin(radian))) + ((y - rows(A) / 2) * (cos(radian)));    # B(newY, newX) = A(y, x);  endforendfor #imshow(B)