### 4. LISSAGE IMAGE ### clc;clear all;close all;A = imread("kawaii_ice.jpg"); % On enregistre l'image sous forme matriciellefor i=2:rows(A)-1   for j=2:columns(A)-1    #           #     #        # o #      #           #      newA(i, j, 1) = (0.25 * A(i, j -1, 1)) +  (0.25 * A(i, j +1, 1)) + (0.25 * A(i - 1, j, 1)) + (0.25 * A(i + 1, j, 1));     newA(i, j, 2) = (0.25 * A(i, j -1, 2)) +  (0.25 * A(i, j +1, 2)) + (0.25 * A(i - 1, j, 2)) + (0.25 * A(i + 1, j, 2));     newA(i, j, 3) = (0.25 * A(i, j -1, 3)) +  (0.25 * A(i, j +1, 3)) + (0.25 * A(i - 1, j, 3)) + (0.25 * A(i + 1, j, 3));  endforendforimshow(newA)